mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY BD_GRP_SID DESC, BD_GRP_LV) AS "NUM" , BD_SID , BD_GRP_SID , BD_GRP_LV , BD_GRP_DEP , BD_TITLE , CASE TO_CHAR(SYSDATE, 'YYYYMMDD') WHEN TO_CHAR(BD_REGI, 'YYYYMMDD') THEN TO_CHAR(BD_REGI, 'HH24:MI') ELSE TO_CHAR(BD_REGI, 'YY.MM.DD') END AS "BD_REGI" , (SELECT COUNT(*) FROM BD_REPL B WHERE B.BD_SID = A.BD_SID) AS "REPLCOUNT" , BD_CLI , BD_DELCK , (SELECT MEM_NICK FROM MEM_INFO A WHERE A.MEM_SID = MEM_SID) AS "MEM_NICK" , BD_LIKE , CASE WHEN BD_BLCK < 1 THEN 'N' ELSE 'Y' END AS "BD_BLCK" FROM BOARD A WHERE BD_DELCK = 'N' )
AND NUM BETWEEN #{startN} AND #{endN}
AND BD_SID = #{bdSid}

mapper.update:

	UPDATE BOARD
	SET BD_GRP_LV = BD_GRP_LV + 1
	WHERE BD_GRP_SID = #{bd_grp_sid} AND BD_GRP_LV > #{bd_grp_LV}

  	UPDATE BOARD
	SET BD_TITLE = #{bdTitle}
		, BD_CONT = #{bdContent}
	WHERE BD_SID = #{bdSid}

  		UPDATE BOARD
		SET BD_DELCK = 'Y'
		WHERE BD_SID = #{bdSid}

  		UPDATE BD_REPL
		SET REPL_BLCK = 'N'
		WHERE BD_SID = #{bdSid}

mapper.select:
  	SELECT COUNT(*) AS "COUNT"
	FROM BOARD_VOTE
	WHERE BD_SID = #{bdSid}
	AND GUBUN_SID = #{gubun_sid}
	AND (BD_VOTE_IP = #{clientIP} OR MEM_SID = #{memSid})

mapper.update:
UPDATE BOARD
SET BD_LIKE = BD_LIKE+1
WHERE BD_SID = #{bdSid}

mapper.select:
  		SELECT BD_LIKE
		FROM BOARD
		WHERE BD_SID = #{bdSid}

mapper.update:
  		UPDATE BOARD
		SET BD_BLCK = BD_BLCK+1
		WHERE BD_SID = #{bdSid}

  		UPDATE BD_REPL
		SET REPL_CONT = #{bd_gesi_repl_cont}
		WHERE REPL_SID = #{bd_gesi_repl_sid}
